import React from 'react';
import {Table,Button} from 'semantic-ui-react';
import {NormalRow} from './NormalRow';
import {RemoveRow} from './RemoveRow';
import EditRow from'./EditRow';

export default class ContactList extends React.Component {

	constructor(props) {
		super(props);
		this.state= {
			removeId:0,
			editId:0,
			firstname:"",
			lastname:"",
			address:"",
			email:"",
			phone:""
		}
	}

	remove = (id) => {
		let tempId = parseInt(id,10);
		if(this.state.removeId === 0) {
			this.setState({
				removeId:tempId,
				editId:0
			})
			return;
		}
		if(tempId === this.state.removeId) {
			this.props.remove(id);
		}
		this.setState({
			removeId:0
		})
	}
	edit = (id) => {
    let tempId = parseInt(id,10);
    if(this.state.editId === 0){
      this.setState({
        editId:tempId,
        removeId:0
      })
      return;
    }
    if (tempId === this.state.editId){
      let contact = {
        id:tempId,
        firstname: this.state.firstname,
        lastname: this.state.lastname,
        address: this.state.address,
        email: this.state.email,
        phone: this.state.phone,
      }
      this.props.editContact(contact,id);
    }
    this.cancel();
  }

	cancel = () => {
		this.setState({
			removeId:0,
			editId:0,
			firstname:"",
			lastname:"",
			address:"",
			email:"",
			phone:""
		})
	}

	onChange = (event) => {
		let state = {};
		state[event.target.name] = event.target.value
		this.setState(state);
	}

	render() {
		if(this.props.list.length === 0) {
			return (<h2>Nothing on the list</h2>)
		} else {
			let listItems = this.props.list.map(item => {
				if(this.state.editId === 0) {
					if(this.state.removeId === item.id) {
						return <RemoveRow key={item.id}
								item={item}
								remove={this.remove}
								cancel={this.cancel}/>
					} else {
						return <NormalRow key={item.id}
								item={item}
								remove={this.remove}
								edit={this.edit}/>
					}
				} else {
					if(this.state.editId === item.id){
            let contact ={
              id:item.id,
              firstname:this.state.firstname,
              lastname:this.state.lastname,
              address:this.state.address,
              email:this.state.email,
              phone:this.state.phone,

            }
            return <EditRow key ={item.id} item={item} save ={this.edit} cancel={this.cancel} onChange={this.onChange}/>
          }else{
            return <NormalRow key={item.id} item={item} remove={this.remove} edit={this.edit}/>
          }
				}
			})

			return (
				<Table>
					<Table.Header>
						<Table.Row>
							<Table.HeaderCell>First Name</Table.HeaderCell>
							<Table.HeaderCell>Last Name</Table.HeaderCell>
							<Table.HeaderCell>Address</Table.HeaderCell>
							<Table.HeaderCell>Email</Table.HeaderCell>
							<Table.HeaderCell>Phone</Table.HeaderCell>
							<Table.HeaderCell>Remove</Table.HeaderCell>
							<Table.HeaderCell>Edit</Table.HeaderCell>
						</Table.Row>
					</Table.Header>
					<Table.Body>
					{listItems}
					</Table.Body>
				</Table>

		)
	}
	}
}
