import React, { Component } from 'react';
import {Table,Button} from 'semantic-ui-react';

export default class ContactList extends Component{
  constructor(props){
    super(props);
    this.state = {
      removeId : 0,
      editId:0,
      firstname:"",
      lastname:"",
      address:"",
      email:"",
      phone:""
    }
  }
  remove = (id) =>{
    let tempId = parseInt(id,10);
    if(this.state.removeId===0){
      this.setState({
        removeId:tempId,
        editId:0
      })
      return;
    }
    if(tempId===this.state.removeId){
    this.props.remove(id);
    }
    this.setState({
      removeId:0
    })

  }
  cancel=()=>{
    this.setState({
      removeId:0,
      editId:0,
      firstname:"",
      lastname:"",
      address:"",
      email:"",
      phone:""
    })
  }

  onChange = (event) => {
    let state = {};
    state[event.target.name] = event.target.value
    this.setState(state);
  }

  render(){
    if(this.props.list.length === 0){
      return ( <h2>Nothing on the list</h2>)
    }else{
    let listItems = this.props.list.map(item =>{
      if(this.state.editId === 0){
        let buttonText="Remove";
        let buttonColor ="gray";
        let editButton =<Table.Cell><Button style={{backgroundColor:buttonColor}} onClick={()=>this.edit()}>Edit</Button></Table.Cell>
        if(item.id===this.state.removeId){
          buttonText="Confirm";
          buttonColor="red";
          editButton =
          <Table.Cell><Button style={{backgroundColor:buttonColor}} onClick={()=>this.cancel()}>Cancel</Button></Table.Cell>
        }
        return <Table.Row key = {item.id}>
          <Table.Cell>{item.firstname}</Table.Cell>
          <Table.Cell>{item.lastname}</Table.Cell>
          <Table.Cell>{item.address}</Table.Cell>
          <Table.Cell>{item.email}</Table.Cell>
          <Table.Cell>{item.phone}</Table.Cell>
          <Table.Cell><Button onClick={()=> this.remove(item.id)}>{buttonText}</Button></Table.Cell>
          {editButton}
        </Table.Row>
      }else{

      }
    })

    return(
      <Table>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>First Name</Table.HeaderCell>
            <Table.HeaderCell>Last Name</Table.HeaderCell>
            <Table.HeaderCell>Address</Table.HeaderCell>
            <Table.HeaderCell>E-mail</Table.HeaderCell>
            <Table.HeaderCell>Phone</Table.HeaderCell>
            <Table.HeaderCell>Remove</Table.HeaderCell>
            <Table.HeaderCell>Edit</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {listItems}
        </Table.Body>
      </Table>
    );
  }
}
}
